import { takeEvery, callÂ } from 'redux-saga/effects';

import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';
import {
    fecthCollectionsSuccess,
    fecthCollectionsFailure
} from './shop.actions'

import ShopActionTypes from './shop.types';

export function* fetchCollectionAsync() {
    yield console.log('its firing');
    try {
        const collectionRef = firestore.collection('collections');
        const snapshot = yield collectionRef.get();
        const collectionMap = call(convertCollectionsSnapshotToMap, snapshot);
    }catch (error) {

    }

    /*
        collectionRef
        .get()
        .then(
            snapshot => {
                const collectionMap = convertCollectionsSnapshotToMap(snapshot);
                dispatch(fecthCollectionsSuccess(collectionMap));
        })
        .catch(error => dispatch(fecthCollectionsFailure(error.message))) */
}

export function* fetchCollectionsStart() {
    yield takeEvery(
        ShopActionTypes.FETCH_COLLECTIONS_START,
        fetchCollectionAsync
        );
}
